icon = PhotoImage(data=icon_data)
root.iconphoto(False, icon)

# how fast the object follows mouse
speed = 0.04

# initialize obj at the center of window
target_x, target_y = 250, 250


def motion(event):
    global target_x, target_y
    # update target position
    target_x = event.x
    target_y = event.y


def move_image():
    global target_x, target_y
    # get current position of the image
    current_coords = canvas.coords(emoji)
    if current_coords:
        current_x = current_coords[0]
        current_y = current_coords[1]

        # calculate new position using linear interpolation
        new_x = current_x + (target_x - current_x) * speed
        new_y = current_y + (target_y - current_y) * speed

        # update object's position
        canvas.coords(emoji, new_x, new_y)

    # continuously call function
    root.after(10, move_image)


canvas = tk.Canvas(root, width=500, height=500)
canvas.pack(fill='both', expand=True)

emoji = canvas.create_image(250, 250, image=icon)

canvas.bind('<Motion>', motion)

# call the move image function and it will start calling itself
move_image()
root.mainloop()
